import { useMemo, useState } from "react";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  const isError = useMemo(() => result.startsWith("Upload failed:"), [result]);

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a PDF!");
      return;
    }

    const formData = new FormData();
    formData.append("pdf", file);

    setLoading(true);
    setResult("");

    try {
      const response = await fetch("http://localhost:8000/api/upload", {
        method: "POST",
        headers: {
          Accept: "application/json",
        },
        body: formData,
      });

      const contentType = response.headers.get("content-type") || "";
      const isJson = contentType.includes("application/json");
      const payload = isJson ? await response.json() : await response.text();

      if (!response.ok) {
        const message =
          isJson && payload?.errors
            ? Object.values(payload.errors).flat().join(" ")
            : isJson && payload?.message
            ? payload.message
            : typeof payload === "string"
            ? payload.slice(0, 300)
            : "Upload failed.";
        throw new Error(message);
      }

      if (!isJson) {
        throw new Error("Server returned non-JSON response. Check backend logs.");
      }

      setResult(payload.output || "No output returned.");
    } catch (error) {
      setResult(`Upload failed: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="app-shell">
      <div className="bg-blob blob-a" aria-hidden="true" />
      <div className="bg-blob blob-b" aria-hidden="true" />

      <section className="glass-card">
        <span className="eyebrow">PDF Extractor</span>
        <h1>Liquid Glass Document Parser</h1>
        <p className="subtitle">
          Upload a PDF and extract clean text output from the backend pipeline. The response below updates instantly after processing.
        </p>

        <div className="controls">
          <label className="file-wrap">
            <input
              type="file"
              accept="application/pdf"
              onChange={(e) => setFile(e.target.files?.[0] || null)}
            />
          </label>
          <button className="upload-btn" onClick={handleUpload} disabled={loading}>
            {loading ? "Processing..." : "Upload & Extract"}
          </button>
        </div>

        <p className="file-name">{file ? `Selected: ${file.name}` : "No file selected."}</p>

        <section className="result-card">
          <h2>Output</h2>
          <pre className={`output ${isError ? "error" : ""} ${result ? "" : "empty"}`}>
            {result || "Your extracted text will appear here."}
          </pre>
        </section>
      </section>
    </main>
  );
}

export default App;
