import { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a PDF!");
      return;
    }

    const formData = new FormData();
    formData.append("pdf", file);

    setLoading(true);
    setResult("");

    try {
      const response = await fetch("http://localhost:8000/api/upload", {
        method: "POST",
        headers: {
          Accept: "application/json",
        },
        body: formData,
      });

      const contentType = response.headers.get("content-type") || "";
      const isJson = contentType.includes("application/json");
      const payload = isJson ? await response.json() : await response.text();

      if (!response.ok) {
        const message =
          isJson && payload?.errors
            ? Object.values(payload.errors).flat().join(" ")
            : isJson && payload?.message
            ? payload.message
            : typeof payload === "string"
            ? payload.slice(0, 300)
            : "Upload failed.";
        throw new Error(message);
      }

      if (!isJson) {
        throw new Error("Server returned non-JSON response. Check backend logs.");
      }

      setResult(payload.output || "No output returned.");
    } catch (error) {
      setResult(`Upload failed: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "900px", margin: "0 auto" }}>
      <h1>PDF Extractor</h1>
      <input
        type="file"
        accept="application/pdf"
        onChange={(e) => setFile(e.target.files?.[0] || null)}
      />
      <button onClick={handleUpload} style={{ marginLeft: "1rem" }} disabled={loading}>
        {loading ? "Processing..." : "Upload & Extract"}
      </button>
      <pre style={{ marginTop: "2rem", whiteSpace: "pre-wrap" }}>{result}</pre>
    </div>
  );
}

export default App;
