import { useState } from "react";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("idle");
  const [errorMessage, setErrorMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a PDF!");
      return;
    }

    const formData = new FormData();
    formData.append("pdf", file);

    setLoading(true);
    setStatus("loading");
    setErrorMessage("");

    try {
      const response = await fetch("http://localhost:8000/api/upload", {
        method: "POST",
        headers: {
          Accept: "application/json",
        },
        body: formData,
      });

      const contentType = response.headers.get("content-type") || "";
      const isJson = contentType.includes("application/json");
      const payload = isJson ? await response.json() : await response.text();

      if (!response.ok) {
        const message =
          isJson && payload?.errors
            ? Object.values(payload.errors).flat().join(" ")
            : isJson && payload?.message
            ? payload.message
            : typeof payload === "string"
            ? payload.slice(0, 300)
            : "Upload failed.";
        throw new Error(message);
      }

      if (!isJson) {
        throw new Error("Server returned non-JSON response. Check backend logs.");
      }

      setStatus("success");
    } catch (error) {
      setStatus("error");
      setErrorMessage(error.message || "Upload failed.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="app-shell">
      <div className="bg-blob blob-a" aria-hidden="true" />
      <div className="bg-blob blob-b" aria-hidden="true" />

      <section className="glass-card">
        <span className="eyebrow">PDF Extractor</span>
        <h1>Liquid Glass Document Parser</h1>
        <p className="subtitle">
          Upload a PDF and extract clean text output from the backend pipeline. The response below updates instantly after processing.
        </p>

        <div className="controls">
          <label className="file-wrap">
            <input
              type="file"
              accept="application/pdf"
              onChange={(e) => setFile(e.target.files?.[0] || null)}
            />
          </label>
          <button className="upload-btn" onClick={handleUpload} disabled={loading}>
            {loading ? "Processing..." : "Upload & Extract"}
          </button>
        </div>

        <p className="file-name">{file ? `Selected: ${file.name}` : "No file selected."}</p>

        <section className="status-card">
          {status === "idle" && <p className="status-hint">Upload a PDF to start extraction.</p>}
          {status === "loading" && <p className="status-hint">Processing your file...</p>}
          {status === "error" && <p className="status-error">Upload failed: {errorMessage}</p>}
          {status === "success" && (
            <div className="success-wrap" role="status" aria-live="polite">
              <span className="success-check" aria-hidden="true" />
              <p className="success-text">Extraction complete</p>
            </div>
          )}
        </section>
      </section>
    </main>
  );
}

export default App;
